CREATE TABLE `#__vm_userfield` (   `fieldid` int(11) NOT NULL auto_increment,   `name` varchar(50) NOT NULL default '',   `title` varchar(255) NOT NULL,   `description` mediumtext NOT NULL,   `type` varchar(50) NOT NULL default '',   `maxlength` int(11) default NULL,   `size` int(11) default NULL,   `required` tinyint(4) default '0',   `ordering` int(11) default NULL,   `cols` int(11) default NULL,   `rows` int(11) default NULL,   `value` varchar(50) default NULL,   `default` int(11) default NULL,   `published` tinyint(1) NOT NULL default '1',   `registration` tinyint(1) NOT NULL default '0',   `shipping` tinyint(1) NOT NULL default '0',   `account` tinyint(1) NOT NULL default '1',   `readonly` tinyint(1) NOT NULL default '0',   `calculated` tinyint(1) NOT NULL default '0',   `sys` tinyint(4) NOT NULL default '0',   `vendor_id` int(11) default NULL,   `params` mediumtext,   PRIMARY KEY  (`fieldid`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='Holds the fields for the user information';
CREATE TABLE `#__vm_userfield_values` (   `fieldvalueid` int(11) NOT NULL auto_increment,   `fieldid` int(11) NOT NULL default '0',   `fieldtitle` varchar(255) NOT NULL default '',   `fieldvalue` varchar(255) NOT NULL,   `ordering` int(11) NOT NULL default '0',   `sys` tinyint(4) NOT NULL default '0',   PRIMARY KEY  (`fieldvalueid`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='Holds the different values for dropdown and radio lists';
CREATE TABLE `#__vm_vendor` (   `vendor_id` int(11) NOT NULL auto_increment,   `vendor_name` varchar(64) default NULL,   `contact_last_name` varchar(32) NOT NULL default '',   `contact_first_name` varchar(32) NOT NULL default '',   `contact_middle_name` varchar(32) default NULL,   `contact_title` varchar(32) default NULL,   `contact_phone_1` varchar(32) NOT NULL default '',   `contact_phone_2` varchar(32) default NULL,   `contact_fax` varchar(32) default NULL,   `contact_email` varchar(255) default NULL,   `vendor_phone` varchar(32) default NULL,   `vendor_address_1` varchar(64) NOT NULL default '',   `vendor_address_2` varchar(64) default NULL,   `vendor_city` varchar(32) NOT NULL default '',   `vendor_state` varchar(32) NOT NULL default '',   `vendor_country` varchar(32) NOT NULL default 'US',   `vendor_zip` varchar(32) NOT NULL default '',   `vendor_store_name` varchar(128) NOT NULL default '',   `vendor_store_desc` text,   `vendor_category_id` int(11) default NULL,   `vendor_thumb_image` varchar(255) default NULL,   `vendor_full_image` varchar(255) default NULL,   `vendor_currency` varchar(16) default NULL,   `cdate` int(11) default NULL,   `mdate` int(11) default NULL,   `vendor_image_path` varchar(255) default NULL,   `vendor_terms_of_service` text NOT NULL,   `vendor_url` varchar(255) NOT NULL default '',   `vendor_min_pov` decimal(10,2) default NULL,   `vendor_freeshipping` decimal(10,2) NOT NULL default '0.00',   `vendor_currency_display_style` varchar(64) NOT NULL default '',   `vendor_accepted_currencies` text NOT NULL,   `vendor_address_format` text NOT NULL,   `vendor_date_format` varchar(255) NOT NULL,   PRIMARY KEY  (`vendor_id`),   KEY `idx_vendor_name` (`vendor_name`),   KEY `idx_vendor_category_id` (`vendor_category_id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='Vendors manage their products in your store';
CREATE TABLE `#__vm_vendor_category` (   `vendor_category_id` int(11) NOT NULL auto_increment,   `vendor_category_name` varchar(64) default NULL,   `vendor_category_desc` text,   PRIMARY KEY  (`vendor_category_id`),   KEY `idx_vendor_category_category_name` (`vendor_category_name`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='The categories that vendors are assigned to';
CREATE TABLE `#__vm_waiting_list` (   `waiting_list_id` int(11) NOT NULL auto_increment,   `product_id` int(11) NOT NULL default '0',   `user_id` int(11) NOT NULL default '0',   `notify_email` varchar(150) NOT NULL default '',   `notified` enum('0','1') default '0',   `notify_date` timestamp NOT NULL default CURRENT_TIMESTAMP on update CURRENT_TIMESTAMP,   PRIMARY KEY  (`waiting_list_id`),   KEY `product_id` (`product_id`),   KEY `notify_email` (`notify_email`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='Stores notifications, users waiting f. products out of stock';
CREATE TABLE `#__vm_zone_shipping` (   `zone_id` int(11) NOT NULL auto_increment,   `zone_name` varchar(255) default NULL,   `zone_cost` decimal(10,2) default NULL,   `zone_limit` decimal(10,2) default NULL,   `zone_description` text NOT NULL,   `zone_tax_rate` int(11) NOT NULL default '0',   PRIMARY KEY  (`zone_id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='The Zones managed by the Zone Shipping Module';
CREATE TABLE `#__weblinks` (   `id` int(11) unsigned NOT NULL auto_increment,   `catid` int(11) NOT NULL default '0',   `sid` int(11) NOT NULL default '0',   `title` varchar(250) NOT NULL default '',   `alias` varchar(255) NOT NULL default '',   `url` varchar(250) NOT NULL default '',   `description` text NOT NULL,   `date` datetime NOT NULL default '0000-00-00 00:00:00',   `hits` int(11) NOT NULL default '0',   `published` tinyint(1) NOT NULL default '0',   `checked_out` int(11) NOT NULL default '0',   `checked_out_time` datetime NOT NULL default '0000-00-00 00:00:00',   `ordering` int(11) NOT NULL default '0',   `archived` tinyint(1) NOT NULL default '0',   `approved` tinyint(1) NOT NULL default '1',   `params` text NOT NULL,   PRIMARY KEY  (`id`),   KEY `catid` (`catid`,`published`,`archived`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;

